import React,{useState, useEffect} from 'react';
import DataTable from 'react-data-table-component';
import MultiSelect from "react-multi-select-component";
import axios from 'axios';

import './bootstrap/dist/css/bootstrap.min.css';
import './App.css';

const API_URL = 'http://localhost:3000';


const App = (props) =>{

  const [getTableFunc, setGetTableFunc] = useState(0);
  const [client_config, set_client_config] = useState({"update_frequency_ms": 3000});
  const [update_frequency_ms, update_frequency_ms_user_input] = useInput({ type: "text",label:"Data Update Frequency(ms): ", id:"update_frequency_ms" });
  const [update_show_frequency_ms, update_show_frequency_ms_user_input] = useInput({ type: "text",label:"Show Frequency(ms): ", id:"update_show_frequency_ms" });
  const [number_of_elements, number_of_elements_user_input] = useInput({ type: "text",label:"Elements per update: ", id:"number_of_elements" });
  const [table, setTable] = useState([]);
  const [selectedSymbols, setSelectedSymbols] = useState([]);
  const [optionsSymbols, setOptionsSymbols] = useState([]);



  function handleReloadTable(){
    clearInterval(getTableFunc);
    if(Number.isFinite(parseInt(update_show_frequency_ms))){
      set_client_config({...client_config,"update_frequency_ms":parseInt(update_show_frequency_ms)});
    }

    setGetTableFunc(setInterval(function(){ 
      axios.get(API_URL+"/getTable").then((res)=>{
        console.log(res);

        setTable((table)=>{
          let result = res.data;
          let n = res.data.length < table.length? res.data.length:table.length;
          for(let i=0; i<n; i++){
            result[i]["prev_price"] = table[i]['price'];
          }
          return result;
        });

      }); 
    }, client_config['update_frequency_ms']));

  }

  useEffect(()=>{
    console.log(table);
  },[table]);

  function useInput({ type, label, id }) {
    const [value, setValue] = useState("");

    const input = (<div><label for={id}>{label}</label>
                    <input id={id} value={value} onChange={e => setValue(e.target.value)} type={type} />
                  </div>);
    return [value, input];
  }

  function handleServerConfig(){
    let server_config = {};
    if(Number.isFinite(parseInt(update_frequency_ms))){
      server_config["update_frequency_milliseconds"] = parseInt(update_frequency_ms)
    }
    if(Number.isFinite(parseInt(number_of_elements))){
      server_config["elements_per_update"] = parseInt(number_of_elements)
    }
    axios.post(API_URL+'/setServerConfig',server_config).then(function(response){
      console.log("success update");
      console.log(response);
    });
  }

  function getServerConfig(){
    axios.get(API_URL+'/getServerConfig').then(function(res){
      console.log(res);
      setOptionsSymbols(symbolsListToMultiSelectOption(res.data)); 
    });
  }

  function symbolsListToMultiSelectOption(arr){
    let output = [];
    for(let i =0; i<arr;++i){
      output.appned({label: arr[i], value:arr[i]});
    }
    return output;
  }

  function multiSelectOptionToSymbolsList(arr){
    let output = [];
    for(let i =0; i<arr;++i){
      output.appned(arr[i].value);
    }
    return output;
  }

  //Table configuration
  const tableConfig = {
    columns:[
      {
        name: 'Symbol',
        selector: 'symbol',
      },
      {
        name: 'Price',
        selector: 'price',
        right: true,
      },
    ],
    conditionalRowStyles:[
      {
        when: row => row.prev_price<row.price,
        style:{
          color: "green"
        },
      },
      {
        when: row => row.prev_price>row.price,
        style:{
          color: "red"
        },
      },    
    ],
  };




  return (
    <div id="App">
      {getServerConfig()}
      <h1>Web GUI</h1>
      <br/>
      <h4>Server Configuration:</h4>
      <MultiSelect
        options={optionsSymbols}
        value={selectedSymbols}
        onChange={setSelectedSymbols}
        labelledBy={"Select Symobols"}
      />
      {update_frequency_ms_user_input}
      {number_of_elements_user_input}
      <button className="btn btn-outline-info" onClick={()=>handleServerConfig()}>
        Update Server Config
      </button>
      <br/>
      <br/>
      <h4>Client GUI Configuration:</h4>
      {update_show_frequency_ms_user_input}



      <button className="btn btn-outline-info" onClick={()=>handleReloadTable()}>
        Reload Table
      </button>
      <br/>
      <br/>


      <div>
        <h2>Price Table:</h2>
        <DataTable
        title="Stock Price Table"
        columns={tableConfig.columns}
        data={table}
        pagination={true}
        striped={true}
        conditionalRowStyles = {tableConfig.conditionalRowStyles}
        dense={true}
        center = {true}
        paginationPerPage={100}
        paginationRowsPerPageOptions={[10, 20, 30, 50, 100]}
        />


      </div>
    </div>
  );
}

export default App;
