const App = (props) =>{
  // const socket = io("http://127.0.0.1:5000/", {path: "" });

  const [getTableFunc, setGetTableFunc] = useState(0);
  const [client_config, set_client_config] = useState({"update_frequency_second": 1});
  const [update_frequency_ms, update_frequency_ms_user_input] = useInput({ type: "text",label:"Update Frequency Milliseconds: ", id:"update_frequency_ms" });
  const [update_show_frequency_ms, update_show_frequency_ms_user_input] = useInput({ type: "text",label:"Update Show Frequency Milliseconds: ", id:"update_show_frequency_ms" });
  const [count, setCount] = useState({"x":0, y:1});
  const [table, setTable] = useState([]);

  // const [prevTable, setprevTable] = useState([]);


  function handleClick(){
    clearInterval(getTableFunc);

    let server_config = {};
    if(Number.isFinite(parseInt(update_frequency_ms))){
      server_config["update_frequency_milliseconds"] = parseInt(update_frequency_ms)
    }

    let freq = 3000;
    if(Number.isFinite(parseInt(update_show_frequency_ms))){
      freq = parseInt(update_show_frequency_ms)
    }

    setGetTableFunc(setInterval(function(){ 

      // setprevTable((prevTable)=>[...table]);
      // console.log(table);
      axios.get(API_URL+"/getTable").then((res)=>{
        console.log(res);

        setTable((table)=>{
          let result = res.data;
          for(let i=0; i<table.length; i++){
            result[i]["prev_price"] = table[i]['price'];
          }
          return result;
        });
        // console.log(table);
      }); 
    }, freq));

  }

  useEffect(()=>{
    console.log(table);
  },[table]);

  function useInput({ type, label, id }) {
    const [value, setValue] = useState("");

    const input = (<div><label for={id}>{label}</label>
                    <input id={id} value={value} onChange={e => setValue(e.target.value)} type={type} />
                  </div>);
    return [value, input];
  }

  return (
    <div>
      <p>You clicked {count["x"]} times</p>
      <button onClick={() => setCount({...count,"x": count["x"]+1})}>
        Click me
      </button>
      <br/>
      <br/>
      Server Config:
      <br/>
      {update_frequency_ms_user_input} -> {update_frequency_ms}<br/>
      <br/>

      User GUI Config:
      <br/>
      {update_show_frequency_ms_user_input}->{update_show_frequency_ms}


      <br/>
      <button onClick={()=>handleClick()}>
        Reloading Table
      </button>


      <div>
        <h2>Element Table:-</h2>
        <table border={2} cellPadding={5}>
           <thead>
              <tr>
                <td>Symbol</td>
                <td>Price</td>
                <td>Price Change</td>
              </tr>
           </thead>
           <tbody>
              {
                  table && 
                  Object.keys(table).map(function (element) {
                    let Color = { color: "red" };
                    if(table[element]){
                      if(table[element]["prev_price"]<=table[element]["price"]){
                        Color = { color: "green" };
                      }
                    }
                    let price_change = Math.round((table[element]["price"]-table[element]["prev_price"])*10)/10;

                     return <tr>
                       <td>{table[element]["symbol"]}</td>
                       <td style={Color}>{table[element]["price"]}</td>
                       <td style={Color}>{price_change}</td>
                     </tr>;
                  })
              }
           </tbody>
        </table>
      </div>
    </div>
  );
}

export default App;
