import React from 'react';

import { MediaQuery } from 'react-responsive'
import DesktopBreakpoint from '../utilities/desktopbreakpoint';
import TabletBreakpoint from '../utilities/tabletbreakpoint';
import PhoneBreakpoint from '../utilities/phonebreakpoint';
import ClientCard from '../components/ClientCard.js';
import {UserAddOutlined} from '@ant-design/icons';
import { Card ,Button} from 'antd';
import {Redirect} from 'react-router-dom';
import renderEmpty from 'antd/lib/config-provider/renderEmpty';



class ClientPageDetail extends React.Component{
    constructor(props){
        super(props);
        this.state={
            redirect: false,
            gender: '',
            name: '',
            location: '',
            logs: [],
            timestart: '',


        };

        this.setConfirmRedirect = this.setConfirmRedirect.bind(this);
        this.renderConfirmRedirect = this.renderConfirmRedirect.bind(this);
    }

    setBackRedirect(){
        this.setState({
            redirect: true,
          });
    }
    renderBackRedirect(){

        if (this.state.redirect == true) {
          return (<Redirect to={{
            pathname: '/',
          }}></Redirect>);
        }
    }

    componentDidMount() {
        fetch("http://localhost:3002/cfg-apac-team29/us-central1/api/getclients")
          .then(res => res.json())
          .then(
            (result) => {            
              for(client in result.clients){
                  if(client.id == this.state.id){
                      this.setState({
                          gender: client.gender,
                          name: client.name,
                          location: client.location,
                          logs: client.logs,
                          timestart: client.timestart,
                      });
                  }
              }

              // console.log(result)
            },
            // Note: it's important to handle errors here
            // instead of a catch() block so that we don't swallow
            // exceptions from actual bugs in components.
            (error) => {
              this.setState({
                isLoaded: true,
                error
              });
              console.log(error);
            }
          )
      }




    render(){
        return(
            <div>
            {this.renderBackRedirect()}

            <PhoneBreakpoint>

            <h1>Matched!</h1>

            <div style={{textAlign:'left' ,marginRight:'10px'}}>
            <Button type="primary" onClick={this.setBackRedirect} >Back</Button>
            </div>

            </PhoneBreakpoint>
            </div>


        )
    }

}

export default ClientPageDetail;
