const App = (props) =>{
  const socket = io("http://127.0.0.1:5000/", {path: "" });
  // const dispatch = useDispatch();
  const [update_frequency_milliseconds, update_frequency_milliseconds_user_input] = useInput({ type: "text",label:"Update Frequency Milliseconds: ", id:"update_frequency_milliseconds" });
  const [count, setCount] = useState({"x":0, y:1});
  const [table, setTable] = useState([
    {
      "symbol": "AAAA",
      "price": 3003
    },
    {
      "symbol": "BBBB",
      "price": 43124
    }

  ]);

  useEffect(() => {
    socket.on('get_table', payload => {
        // console.log(payload);
        setTable(payload);
    });
  });

  function handleClick(){
    socket.emit('start_get_table',null);
  }

  function useInput({ type, label, id }) {
    const [value, setValue] = useState("");

    const input = (<div><label for={id}>{label}</label>
                    <input id={id} value={value} onChange={e => setValue(e.target.value)} type={type} />
                  </div>);
    return [value, input];
  }

  return (
    <div>
      <p>You clicked {count["x"]} times</p>
      <button onClick={() => setCount({...count,"x": count["x"]+1})}>
        Click me
      </button>

      <button onClick={()=>handleClick()}>
        Start Loading Stock Price Table
      </button>
      <br/>

      {update_frequency_milliseconds_user_input} -> {update_frequency_milliseconds}


      <div>
        <h2>Output:-</h2>
        <table border={2} cellPadding={5}>
